import React, { useState, useEffect } from "react";
import { countries } from "countries-list"; // Import countries-list package

const Weather = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude: lat, longitude: lon } = position.coords; // Destructure latitude and longitude

          try {
            const response = await fetch(
              `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=972112e503a63e5c3e7426ddbb881612`
            );

            if (!response.ok) {
              throw new Error("Weather data request failed");
            }

            const data = await response.json();

            setWeatherData(data);
            setLoading(false);
          } catch (error) {
            console.error("Error fetching or processing weather data:", error);
            setError("Failed to fetch weather data.");
            setLoading(false);
          }
        },
        (error) => {
          console.error("Error getting geolocation:", error);
          setError("Failed to retrieve geolocation.");
          setLoading(false);
        }
      );
    } else {
      console.error("Geolocation is not available in this browser.");
      setError("Geolocation is not supported in your browser.");
      setLoading(false);
    }
  }, []);

  return (
    <div className="h-[645px] flex items-center justify-center relative">
      {/* Video background code */}
      {/* Your existing video element */}

      <div className="bg-gray-200 m-[10px] border border-black p-6 rounded-lg text-center">
        <h1 className="text-2xl  font-mono md:text-3xl lg:text-4xl font-semibold text-blue-500 mb-4">
          Weather Information
        </h1>

        {loading ? (
          <p className="text-green-500 font-mono  text-lg">Loading weather data...</p>
        ) : error ? (
          <p className="text-red-500 text-lg">{error}</p>
        ) : weatherData ? (
          <div className="p-[10px]">
            <h2 className="text-xl font-mono font-bold text-green-700 mb-2">
              Location: {weatherData.name}
            </h2>
            <h2 className="text-xl  font-mono font-bold text-green-700 mb-2">
              Country: {countries[weatherData.sys.country]?.name || 'Unknown'}
            </h2>
            <ul className="list-disc">
              <li className="text-black font-mono font-semibold text-lg mb-2">
                Weather: {weatherData.weather[0].description}
              </li>
              <li className="text-black font-mono font-semibold text-lg mb-2">
                Temperature: {(weatherData.main.temp - 273.15).toFixed(3)}째C
              </li>
              <li className="text-black font-mono font-semibold text-lg mb-2">
                Feels Like: {(weatherData.main.feels_like- 273.15).toFixed(3)}째C
              </li>
              <li className="text-black font-mono font-semibold text-lg mb-2">
                Max Temperature: {(weatherData.main.temp_max- 273.15).toFixed(3)}째C
              </li>
              <li className="text-black font-mono font-semibold text-lg mb-2">
                Min Temperature: {(weatherData.main.temp_min- 273.15).toFixed(3)}째C
              </li>
              <li className="text-black font-mono font-semibold text-lg mb-2">
                Air pressure: {weatherData.main.pressure} mb
              </li>
              <li className="text-black font-mono font-semibold text-lg mb-2">
                Humidity: {weatherData.main.humidity}%
              </li>
              <li className="text-black font-mono font-semibold text-lg mb-2">
                Wind Speed: {(weatherData.wind.speed / 1000).toFixed(3)} Km/s
              </li>
            </ul>
          </div>
        ) : (
          <p className="text-red-500 font-mono text-lg">Failed to fetch weather data.</p>
        )}
      </div>
    </div>
  );
};

export default Weather;
