// src/components/Weather.js
import React, { useState, useEffect } from 'react';

const Weather = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if geolocation is available in the browser
    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition(async (position) => {
        const { latitude, longitude } = position.coords;

        try {
          // Fetch weather data using the obtained coordinates
          const response = await fetch(
            `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=YOUR_API_KEY`
          );
          const data = await response.json();

          setWeatherData(data);
          setLoading(false);
        } catch (error) {
          console.error('Error fetching weather data:', error);
          setLoading(false);
        }
      }, (error) => {
        console.error('Error getting geolocation:', error);
        setLoading(false);
      });
    } else {
      console.error('Geolocation is not available in this browser.');
      setLoading(false);
    }
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-semibold text-center mb-4">Weather Information</h1>
      {loading ? (
        <p className="text-center">Loading weather data...</p>
      ) : weatherData ? (
        <div className="bg-blue-100 p-6 rounded-lg shadow-lg">
          <h2 className="text-xl font-semibold mb-2">{weatherData.name}, {weatherData.sys.country}</h2>
          <p className="text-gray-700 mb-2">Weather: {weatherData.weather[0].description}</p>
          <p className="text-gray-700 mb-2">Temperature: {weatherData.main.temp}Â°C</p>
          <p className="text-gray-700 mb-2">Humidity: {weatherData.main.humidity}%</p>
          <p className="text-gray-700 mb-2">Wind Speed: {weatherData.wind.speed} m/s</p>
        </div>
      ) : (
        <p className="text-center text-red-500">Failed to fetch weather data.</p>
      )}
    </div>
  );
};

export default Weather;
