import React, { useState, useEffect } from 'react';

const Weather = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition(async (position) => {
        const { latitude, longitude } = position.coords;

        try {
          const response = await fetch(
            `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=56a1ed2611bd3972271b413653c7bb8d`
          );

          if (!response.ok) {
            throw new Error('Weather data request failed');
          }

          const data = await response.json();

          if (!data.name || !data.sys || !data.weather || !data.main || !data.wind) {
            throw new Error('Weather data structure is incomplete');
          }

          setWeatherData(data);
          setLoading(false);
        } catch (error) {
          console.error('Error fetching or processing weather data:', error);
          setError('Failed to fetch weather data.');
          setLoading(false);
        }
      }, (error) => {
        console.error('Error getting geolocation:', error);
        setError('Failed to retrieve geolocation.');
        setLoading(false);
      });
    } else {
      console.error('Geolocation is not available in this browser.');
      setError('Geolocation is not supported in your browser.');
      setLoading(false);
    }
  }, []);

  return (
    <div className="bg-gradient-to-r from-blue-500 to-purple-500 min-h-screen flex items-center justify-center">
      <div className="bg-white p-6 rounded-lg shadow-lg text-center">
        <h1 className="text-3xl font-semibold text-gray-800 mb-4">Weather Information</h1>
        {loading ? (
          <p className="text-gray-600">Loading weather data...</p>
        ) : error ? (
          <p className="text-red-500">{error}</p>
        ) : weatherData ? (
          <div>
            <h2 className="text-2xl font-semibold text-gray-800 mb-2">
              {weatherData.name}, {weatherData.sys.country}
            </h2>
            <p className="text-gray-600 text-lg mb-2">
              Weather: {weatherData.weather[0].description}
            </p>
            <p className="text-gray-600 text-lg mb-2">
              Temperature: {weatherData.main.temp}K
            </p>
            <p className="text-gray-600 text-lg mb-2">
              Humidity: {weatherData.main.humidity}%
            </p>
            <p className="text-gray-600 text-lg mb-2">
              Wind Speed: {weatherData.wind.speed} m/s
            </p>
          </div>
        ) : (
          <p className="text-red-500">Failed to fetch weather data.</p>
        )}
      </div>
    </div>
  );
};

export default Weather;
