// Routes.js
import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom"; // Make sure you import Routes from react-router-dom
import Navbar from "./Navbar";
import Home from "./Home";
import Weather from "./Weather";
import CropPrediction from "./CropPrediction";
import FertilizerPrediction from "./FertilizerPrediction";
import DiseasePrediction from "./DiseasePrediction";
import Login from "./Login";
import ProtectedRoute from "./ProtectedRoute";

const AppRoutes = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Function to handle successful login
  const handleSuccessfulLogin = () => {
    setIsLoggedIn(true);
    console.log("User logged in successfully.");
  };

  // Function to handle logout
  const handleLogout = () => {
    setIsLoggedIn(false);
    console.log("User logged out.");
  };

  return (
    <Router>
      <Navbar isLoggedIn={isLoggedIn} handleLogout={handleLogout} />
      <Routes>
        {/* Public routes */}
        <Route path="/login" element={<Login handleSuccessfulLogin={handleSuccessfulLogin} />} />
        <Route path="/" element={<Home />} />

        {/* Protected routes */}
        <ProtectedRoute path="/weather" element={<Weather />} isLoggedIn={isLoggedIn} />
        <ProtectedRoute path="/crop-prediction" element={<CropPrediction />} isLoggedIn={isLoggedIn} />
        <ProtectedRoute path="/fertilizer-prediction" element={<FertilizerPrediction />} isLoggedIn={isLoggedIn} />
        <ProtectedRoute path="/disease-prediction" element={<DiseasePrediction />} isLoggedIn={isLoggedIn} />

        {/* Redirect logged-in users to home if they try to access login page */}
        {isLoggedIn && <Route path="/login" element={<Navigate to="/" replace={true} />} />}

        {/* Redirect logged-out users to login page if they try to access protected routes */}
        {!isLoggedIn && <Route path="*" element={<Navigate to="/login" replace={true} />} />}
      </Routes>
    </Router>
  );
};

export default AppRoutes;
