// src/components/Routes.js
import React, { useState } from 'react';
import { BrowserRouter, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './Navbar';
import Home from './Home';
import Weather from './Weather';
import CropPrediction from './CropPrediction';
import FertilizerPrediction from './FertilizerPrediction';
import DiseasePrediction from './DiseasePrediction';
import Login from './Login';

const AppRoutes = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Function to handle successful login
  const handleLogin = () => {
    // Implement your logic for successful login
    setIsLoggedIn(true);
  };

  // If user is logged in, redirect to the home page, else render the login page
  const renderLoginOrHome = () => {
    return isLoggedIn ? <Navigate to="/" /> : <Login onLogin={handleLogin} />;
  };

  return (
    <BrowserRouter>
      <Navbar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={renderLoginOrHome} />
        <Route path="/weather" element={<Weather />} />
        <Route path="/crop-prediction" element={<CropPrediction />} />
        <Route path="/fertilizer-prediction" element={<FertilizerPrediction />} />
        <Route path="/disease-prediction" element={<DiseasePrediction />} />
      </Routes>
    </BrowserRouter>
  );
};

export default AppRoutes;
