import React from "react";
import { BrowserRouter, Route, Routes, Navigate } from "react-router-dom";
import Navbar from "./Navbar";
import Home from "./Home";
import Weather from "./Weather";
import CropPrediction from "./CropPrediction";
import FertilizerPrediction from "./FertilizerPrediction";
import DiseasePrediction from "./DiseasePrediction";
import Login from "./Login";
import NotFound from "./NotFound";

const ProtectedRoute = ({ element: Element, isLoggedIn, ...rest }) => {
  return isLoggedIn ? (
    <Route {...rest} element={<Element />} />
  ) : (
    // Debug: Add console log to check navigation
    console.log("Redirecting to login..."),
    <Navigate to="/login" />
  );
};

const AppRoutes = () => {
  const [isLoggedIn, setIsLoggedIn] = React.useState(false);

  // Debug: Add console log to check login state
  console.log("isLoggedIn:", isLoggedIn);

  return (
    <BrowserRouter>
      <Navbar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
        <ProtectedRoute path="/weather" element={<Weather />} isLoggedIn={isLoggedIn} />
        <ProtectedRoute path="/crop-prediction" element={<CropPrediction />} isLoggedIn={isLoggedIn} />
        <ProtectedRoute
          path="/fertilizer-prediction"
          element={<FertilizerPrediction />}
          isLoggedIn={isLoggedIn}
        />
        <ProtectedRoute
          path="/disease-prediction"
          element={<DiseasePrediction />}
          isLoggedIn={isLoggedIn}
        />
        <Route path="*" element={<NotFound />} />
      </Routes>
    </BrowserRouter>
  );
};

export default AppRoutes;
