import React, { useState } from 'react';
import axios from 'axios';

const DiseasePrediction = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [prediction, setPrediction] = useState('');
  const [loading, setLoading] = useState(false);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleSubmit = async () => {
    setLoading(true);
    const formData = new FormData();
    formData.append('image', selectedFile);

    try {
      const response = await axios.post('http://127.0.0.1:5003/predict', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setPrediction(response.data.predicted_class);
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4 bg-white rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold mb-4"> Rice-Disease Prediction</h2>
      <div className="mb-4">
        <input type="file" accept="image/jpeg" onChange={handleFileChange} className="border border-gray-300 rounded p-2" />
        <button className="bg-blue-500 text-white py-2 px-4 ml-2 rounded" onClick={handleSubmit} disabled={!selectedFile || loading}>
          {loading ? 'Predicting...' : 'Predict'}
        </button>
      </div>
      {prediction && (
        <div>
          <h3 className="text-lg  text-green-font-semibold">Predicted Disease:</h3>
          <p>{prediction}</p>
        </div>
      )}
    </div>
  );
};

export default DiseasePrediction;
