import React from 'react';

const Home = () => {
  const containerStyle = {
    backgroundColor: '#f0f0f0', // Background color
    padding: '4rem', // Add padding for spacing
    textAlign: 'center',
  };

  const titleStyle = {
    fontSize: '3rem', // Font size for the title
    fontWeight: 'bold', // Bold font weight
    color: 'green', // Text color
  };

  const visionStyle = {
    marginTop: '2rem', // Add margin to separate the title and vision portion
    fontSize: '1.5rem', // Font size for the vision portion
    lineHeight: '1.5', // Line height for better readability
  };

  return (
    <div style={containerStyle}>
      <h1 style={titleStyle} className="animate__animated animate__fadeIn">
        Agritech Insight
      </h1>
      <div style={visionStyle}>
        <p>
          Welcome to Agritech Insight, your trusted source for agricultural information and innovation.
        </p>
        <p>
          Our vision is to revolutionize the agricultural industry through technology and sustainable practices.
        </p>
        <p>
          Join us in shaping the future of farming!
        </p>
      </div>
    </div>
  );
};

export default Home;
