import React, { useState } from 'react';
import axios from 'axios';

const DiseasePrediction = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [prediction, setPrediction] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setError('');
  };

  const handleSubmit = async () => {
    if (!selectedFile || !selectedFile.type.startsWith('image/jpeg')) {
      setError('Please choose a JPEG image file.');
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('image', selectedFile);

    try {
      const response = await axios.post('http://127.0.0.1:5003/predict', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setPrediction(response.data.predicted_class);
    } catch (error) {
      console.error('Error:', error);
      setError('An error occurred while predicting the disease.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex justify-center pt-4 items-center h-full">
      <div className="p-4 bg-blue-100 border border-black rounded-lg shadow-lg">
        <h2 className="text-2xl font-bold mb-4">Rice-Disease Prediction</h2>
        <div className="mb-4">
          <input type="file" accept="image/jpeg" onChange={handleFileChange} className="border border-gray-300 rounded p-2" />
          <button className="bg-blue-500 rounded-md text-white py-2 px-4 ml-2" onClick={handleSubmit} disabled={!selectedFile || loading}>
            {loading ? 'Predicting...' : 'Predict'}
          </button>
          {error && <p className="text-red-500font-mono">{error}</p>}
        </div>
        {prediction && (
          <div>
            <h3 className="text-lg text-green-500 font-mono font-semibold">Predicted Disease:</h3>
            <p>{prediction}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default DiseasePrediction;
