import React, { useState } from "react";
import axios from "axios";

const FertilizerPrediction = () => {
  const [temperature, setTemperature] = useState("");
  const [humidity, setHumidity] = useState("");
  const [moisture, setMoisture] = useState("");
  const [soilType, setSoilType] = useState("");
  const [cropType, setCropType] = useState("");
  const [nitrogen, setNitrogen] = useState("");
  const [potassium, setPotassium] = useState("");
  const [phosphorus, setPhosphorus] = useState("");
  const [fertilizerResult, setFertilizerResult] = useState("");
  const [error, setError] = useState("");

  const handleSoilTypeChange = (e) => {
    setSoilType(e.target.value);
  };

  const handleCropTypeChange = (e) => {
    setCropType(e.target.value);
  };

  const handleSubmit = async () => {
    try {
      // Construct the JSON data object
      const data = {
        Temperature: [parseFloat(temperature)],
        Humidity: [parseFloat(humidity)],
        Moisture: [parseFloat(moisture)],
        "Soil Type": [soilType],
        "Crop Type": [cropType],
        Nitrogen: [parseFloat(nitrogen)],
        Potassium: [parseFloat(potassium)],
        Phosphorus: [parseFloat(phosphorus)],
      };
      
      const response = await axios.post(
        "https://predict-fertilizer-api.onrender.com/predict",
        data
      );

      console.log("API Response:", response);

      if (response.data && response.data.predicted_fertilizer) {
        setFertilizerResult(response.data.predicted_fertilizer);
        setError(""); // Clear any previous errors
      } else {
        setError("Predicted fertilizer value not found in the response.");
      }
    } catch (error) {
      setError(
        "An error occurred while processing your request. Please try again."
      );
      console.error("Error:", error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-semibold text-center mb-4">
        FERTILIZER PREDICTION
      </h1>
      <div className="bg-blue-100 border border-blue-700 p-6 rounded-lg shadow-lg">
        <div className="grid grid-cols-2 gap-4">
          {/* Input fields for fertilizer prediction */}
          {/* Temperature */}
          <div className="col-span-2 sm:col-span-1">
            <label
              className="block text-gray-700 font-bold mb-2"
              htmlFor="temperature"
            >
              Temperature (°C):
            </label>
            <input
              type="text"
              id="temperature"
              placeholder="Enter Temperature in °C"
              className="w-full bg-white border border-black rounded-lg p-2 outline-none"
              value={temperature}
              onChange={(e) => setTemperature(e.target.value)}
            />
          </div>
          {/* Humidity */}
          <div className="col-span-2 sm:col-span-1">
            <label
              className="block text-gray-700 font-bold mb-2"
              htmlFor="humidity"
            >
              Humidity (%):
            </label>
            <input
              type="text"
              id="humidity"
              placeholder="Enter Humidity in %"
              className="w-full bg-white border border-black rounded-lg p-2 outline-none"
              value={humidity}
              onChange={(e) => setHumidity(e.target.value)}
            />
          </div>
          {/* Moisture */}
          <div className="col-span-2 sm:col-span-1">
            <label
              className="block text-gray-700 font-bold mb-2"
              htmlFor="moisture"
            >
              Moisture:
            </label>
            <input
              type="text"
              id="moisture"
              placeholder="Enter Moisture"
              className="w-full bg-white border border-black rounded-lg p-2 outline-none"
              value={moisture}
              onChange={(e) => setMoisture(e.target.value)}
            />
          </div>
          {/* Soil Type */}
          <div className="col-span-2 sm:col-span-1">
            <label
              className="block text-gray-700 font-bold mb-2"
              htmlFor="soilType"
            >
              Soil Type:
            </label>
            <select
              id="soilType"
              className="w-full bg-white border border-black rounded-lg p-2 outline-none"
              value={soilType}
              onChange={handleSoilTypeChange}
            >
              <option value="">Select Soil Type</option>
              <option value="Loamy">Loamy</option>
              <option value="Sandy">Sandy</option>
              <option value="Clayey">Clayey</option>
              <option value="Black">Black</option>
              <option value="Red">Red</option>
            </select>
          </div>
          {/* Crop Type */}
          <div className="col-span-2 sm:col-span-1">
            <label
              className="block text-gray-700 font-bold mb-2"
              htmlFor="cropType"
            >
              Crop Type:
            </label>
            <select
              id="cropType"
              className="w-full bg-white border border-black rounded-lg p-2 outline-none"
              value={cropType}
              onChange={handleCropTypeChange}
            >
              <option value="">Select Crop Type</option>
              <option value="Sugarcane">Sugarcane</option>
              <option value="Cotton">Cotton</option>
              <option value="Millets">Millets</option>
              <option value="Paddy">Paddy</option>
              <option value="Pulses">Pulses</option>
            </select>
          </div>
          {/* Nitrogen */}
          <div className="col-span-2 sm:col-span-1">
            <label
              className="block text-gray-700 font-bold mb-2"
              htmlFor="nitrogen"
            >
              Nitrogen (%):
            </label>
            <input
              type="text"
              id="nitrogen"
              placeholder="Enter Nitrogen Percentage"
              className="w-full bg-white border border-black rounded-lg p-2 outline-none"
              value={nitrogen}
              onChange={(e) => setNitrogen(e.target.value)}
            />
          </div>
          {/* Potassium */}
          <div className="col-span-2 sm:col-span-1">
            <label
              className="block text-gray-700 font-bold mb-2"
              htmlFor="potassium"
            >
              Potassium (%):
            </label>
            <input
              type="text"
              id="potassium"
              placeholder="Enter Potassium Percentage"
              className="w-full bg-white border border-black rounded-lg p-2 outline-none"
              value={potassium}
              onChange={(e) => setPotassium(e.target.value)}
            />
          </div>
          {/* Phosphorus */}
          <div className="col-span-2 sm:col-span-1">
            <label
              className="block text-gray-700 font-bold mb-2"
              htmlFor="phosphorus"
            >
              Phosphorus (%):
            </label>
            <input
              type="text"
              id="phosphorus"
              placeholder="Enter Phosphorus Percentage"
              className="w-full bg-white border border-black rounded-lg p-2 outline-none"
              value={phosphorus}
              onChange={(e) => setPhosphorus(e.target.value)}
            />
          </div>
          {/* Submit Button */}
          <div className="col-span-2">
            <button
              className="bg-blue-500 hover:bg-blue-600 text-white p-3 rounded-lg w-[60%] sm:w-64 mx-auto block"
              onClick={handleSubmit}
            >
              Submit
            </button>
          </div>
        </div>
      </div>
      {/* Render fertilizer prediction result */}
      {!error && fertilizerResult && (
        <div className="mt-4">
          <p className="text-center text-xl font-bold font-mono text-green-700">
            Recommended Fertilizer: {fertilizerResult}
          </p>
        </div>
      )}
      {/* Render error message if there is an error */}
      {error && (
        <div className="mt-4">
          <p className="text-center font-mono font-bold text-red-500">{error}</p>
        </div>
      )}
    </div>
  );
};

export default FertilizerPrediction;
