import React, { useState, useEffect } from 'react';

const Weather = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Check if geolocation is available in the browser
    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition(async (position) => {
        const { latitude, longitude } = position.coords;
        const apiKey = 'fcedbfbca55a7972cc160ba581ef6b94'; 

        try {
          // Fetch weather data using the obtained coordinates and your API key
          const response = await fetch(
            `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=${apiKey}`
          );

          if (!response.ok) {
            throw new Error('Weather data request failed');
          }

          const data = await response.json();

          // Check if the expected properties exist in the response
          if (!data.name || !data.weather || !data.main || !data.wind) {
            throw new Error('Weather data structure is incomplete');
          }

          setWeatherData(data);
          setLoading(false);
        } catch (error) {
          console.error('Error fetching or processing weather data:', error);
          setError('Failed to fetch weather data.');
          setLoading(false);
        }
      }, (error) => {
        console.error('Error getting geolocation:', error);
        setError('Failed to retrieve geolocation.');
        setLoading(false);
      });
    } else {
      console.error('Geolocation is not available in this browser.');
      setError('Geolocation is not supported in your browser.');
      setLoading(false);
    }
  }, []);

  // Function to get a human-readable description of the weather condition
  const getWeatherDescription = () => {
    if (weatherData && weatherData.weather && weatherData.weather.length > 0) {
      return weatherData.weather[0].description;
    }
    return 'N/A';
  };

  // Function to predict rain or thunderstorm
  const predictRainOrThunderstorm = () => {
    if (weatherData && weatherData.weather && weatherData.weather.length > 0) {
      const weatherCode = weatherData.weather[0].id;
      if (weatherCode >= 200 && weatherCode <= 232) {
        return 'Thunderstorm expected';
      }
      if (weatherCode >= 300 && weatherCode <= 531) {
        return 'Rain expected';
      }
    }
    return 'No rain or thunderstorm expected';
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-semibold text-center mb-4">Weather Information</h1>
      {loading ? (
        <p className="text-center">Loading weather data...</p>
      ) : error ? (
        <p className="text-center text-red-500">{error}</p>
      ) : weatherData ? (
        <div className="bg-blue-100 p-6 rounded-lg shadow-lg">
          <h2 className="text-xl font-semibold mb-2">{weatherData.name}</h2>
          <p className="text-gray-700 mb-2">{getWeatherDescription()}</p>
          <p className="text-gray-700 mb-2">Temperature: {weatherData.main.temp}°C</p>
          <p className="text-gray-700 mb-2">Humidity: {weatherData.main.humidity}%</p>
          <p className="text-gray-700 mb-2">Wind Speed: {weatherData.wind.speed} m/s</p>
          <p className="text-red-600 font-semibold">{predictRainOrThunderstorm()}</p>
        </div>
      ) : (
        <p className="text-center text-red-500">Failed to fetch weather data.</p>
      )}
    </div>
  );
};

export default Weather;
