import React, { useState, useEffect } from 'react';

const Weather = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Check if geolocation is available in the browser
    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition(async (position) => {
        const { latitude, longitude } = position.coords;
        const apiKey = 'YOUR_WEATHERSTACK_API_KEY'; // Replace with your Weatherstack API key

        try {
          // Fetch weather data using the obtained coordinates and your Weatherstack API key
          const response = await fetch(
            `http://api.weatherstack.com/current?access_key=${apiKey}&query=${latitude},${longitude}`
          );

          if (!response.ok) {
            throw new Error('Weather data request failed');
          }

          const data = await response.json();

          // Check if the expected properties exist in the response
          if (!data.location || !data.current) {
            throw new Error('Weather data structure is incomplete');
          }

          setWeatherData(data);
          setLoading(false);
        } catch (error) {
          console.error('Error fetching or processing weather data:', error);
          setError('Failed to fetch weather data.');
          setLoading(false);
        }
      }, (error) => {
        console.error('Error getting geolocation:', error);
        setError('Failed to retrieve geolocation.');
        setLoading(false);
      });
    } else {
      console.error('Geolocation is not available in this browser.');
      setError('Geolocation is not supported in your browser.');
      setLoading(false);
    }
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-semibold text-center mb-4">Weather Information</h1>
      {loading ? (
        <p className="text-center">Loading weather data...</p>
      ) : error ? (
        <p className="text-center text-red-500">{error}</p>
      ) : weatherData ? (
        <div className="bg-blue-100 p-6 rounded-lg shadow-lg">
          <div className="text-center mb-2">
            <h2 className="text-2xl font-semibold">
              {weatherData.location.name}, {weatherData.location.country}
            </h2>
            <p className="text-gray-700">{weatherData.current.weather_descriptions[0]}</p>
          </div>
          <div className="flex justify-between items-center">
            <div>
              <p className="text-gray-700">Temperature: {weatherData.current.temperature}°C</p>
              <p className="text-gray-700">Humidity: {weatherData.current.humidity}%</p>
              <p className="text-gray-700">Wind Speed: {weatherData.current.wind_speed} m/s</p>
            </div>
            <div>
              <p className="text-gray-700">
                Feels Like: {weatherData.current.feelslike}°C
              </p>
              <p className="text-gray-700">
                UV Index: {weatherData.current.uv_index}
              </p>
            </div>
          </div>
          <div className="mt-4">
            {weatherData.current.weather_code === 200 ? (
              <p className="text-green-600 font-semibold">No thunderstorms expected</p>
            ) : (
              <p className="text-red-600 font-semibold">Thunderstorms possible</p>
            )}
          </div>
        </div>
      ) : (
        <p className="text-center text-red-500">Failed to fetch weather data.</p>
      )}
    </div>
  );
};

export default Weather;
