import React, { useState } from "react";
import axios from "axios";

const FertilizerPrediction = () => {
  const [temperature, setTemperature] = useState("");
  const [humidity, setHumidity] = useState("");
  const [moisture, setMoisture] = useState("");
  const [soilType, setSoilType] = useState([]);
  const [cropType, setCropType] = useState([]);
  const [nitrogen, setNitrogen] = useState("");
  const [potassium, setPotassium] = useState("");
  const [phosphorus, setPhosphorus] = useState("");
  const [fertilizerResult, setFertilizerResult] = useState("");
  const [error, setError] = useState("");

  const handleSoilTypeChange = (event) => {
    const { value, checked } = event.target;
    if (checked) {
      setSoilType((prevValues) => [...prevValues, value]);
    } else {
      setSoilType((prevValues) => prevValues.filter((type) => type !== value));
    }
  };

  const handleCropTypeChange = (event) => {
    const { value, checked } = event.target;
    if (checked) {
      setCropType((prevValues) => [...prevValues, value]);
    } else {
      setCropType((prevValues) => prevValues.filter((type) => type !== value));
    }
  };

  const handleSubmit = async () => {
    try {
      // Construct the JSON data object
      const data = {
        Temparature: [parseFloat(temperature)],
        "Humidity ": [parseFloat(humidity)], // Keep the key as it is with the extra space
        Moisture: [parseFloat(moisture)],
        "Soil Type": [parseString(soilType)],
        "Crop Type": [cropType],
        Nitrogen: [parseFloat(nitrogen)],
        Potassium: [parseFloat(potassium)],
        Phosphorous: [parseFloat(phosphorus)],
      };

      const response = await axios.post(
        "https://predict-fertilizer-api.onrender.com/predict",
        data
      );

      console.log("API Response:", response);

      if (response.data && response.data.predicted_fertilizer) {
        setFertilizerResult(response.data.predicted_fertilizer);
        setError(""); // Clear any previous errors
      } else {
        setError("Predicted fertilizer value not found in the response.");
      }
    } catch (error) {
      setError(
        "An error occurred while processing your request. Please try again."
      );
      console.error("Error:", error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-semibold text-center mb-4">
        FERTILIZER PREDICTION
      </h1>
      <div className="bg-blue-100 border border-blue-700 p-6 rounded-lg shadow-lg">
        <div className="grid grid-cols-2 gap-4">
          {/* Input fields for fertilizer prediction */}
          {/* Temperature */}
          <div className="col-span-2 sm:col-span-1">
            <label
              className="block text-gray-700 font-bold mb-2"
              htmlFor="temperature"
            >
              Temperature (°C):
            </label>
            <input
              type="text"
              id="temperature"
              placeholder="Enter Temperature in °C"
              className="w-full bg-white border border-black rounded-lg p-2 outline-none"
              value={temperature}
              onChange={(e) => setTemperature(e.target.value)}
            />
          </div>
          {/* Humidity */}
          <div className="col-span-2 sm:col-span-1">
            <label
              className="block text-gray-700 font-bold mb-2"
              htmlFor="humidity"
            >
              Humidity (%):
            </label>
            <input
              type="text"
              id="humidity"
              placeholder="Enter Humidity in %"
              className="w-full bg-white border border-black rounded-lg p-2 outline-none"
              value={humidity}
              onChange={(e) => setHumidity(e.target.value)}
            />
          </div>
          {/* Moisture */}
          <div className="col-span-2 sm:col-span-1">
            <label
              className="block text-gray-700 font-bold mb-2"
              htmlFor="moisture"
            >
              Moisture:
            </label>
            <input
              type="text"
              id="moisture"
              placeholder="Enter Moisture"
              className="w-full bg-white border border-black rounded-lg p-2 outline-none"
              value={moisture}
              onChange={(e) => setMoisture(e.target.value)}
            />
          </div>
          {/* Soil Type */}
          <div className="col-span-2 sm:col-span-1">
            <label className="block text-gray-700 font-bold mb-2">
              Soil Type:
            </label>
            <div>
              <label htmlFor="loamy" className="mr-2">
                Loamy
                <input
                  type="checkbox"
                  id="loamy"
                  value="Loamy"
                  onChange={handleSoilTypeChange}
                />
              </label>
              <label htmlFor="sandy" className="mr-2">
                Sandy
                <input
                  type="checkbox"
                  id="sandy"
                  value="Sandy"
                  onChange={handleSoilTypeChange}
                />
              </label>
              <label htmlFor="clayey" className="mr-2">
                Clayey
                <input
                  type="checkbox"
                  id="clayey"
                  value="Clayey"
                  onChange={handleSoilTypeChange}
                />
              </label>
              <label htmlFor="black" className="mr-2">
                Black
                <input
                  type="checkbox"
                  id="black"
                  value="Black"
                  onChange={handleSoilTypeChange}
                />
              </label>
              <label htmlFor="red">
                Red
                <input
                  type="checkbox"
                  id="red"
                  value="Red"
                  onChange={handleSoilTypeChange}
                />
              </label>
            </div>
          </div>
          {/* Crop Type */}
          <div className="col-span-2 sm:col-span-1">
            <label className="block text-gray-700 font-bold mb-2">
              Crop Type:
            </label>
            <div>
              <label htmlFor="sugarcane" className="mr-2">
                Sugarcane
                <input
                  type="checkbox"
                  id="sugarcane"
                  value="Sugarcane"
                  onChange={handleCropTypeChange}
                />
              </label>
              <label htmlFor="cotton" className="mr-2">
                Cotton
                <input
                  type="checkbox"
                  id="cotton"
                  value="Cotton"
                  onChange={handleCropTypeChange}
                />
              </label>
              <label htmlFor="millets" className="mr-2">
                Millets
                <input
                  type="checkbox"
                  id="millets"
                  value="Millets"
                  onChange={handleCropTypeChange}
                />
              </label>
              <label htmlFor="paddy" className="mr-2">
                Paddy
                <input
                  type="checkbox"
                  id="paddy"
                  value="Paddy"
                  onChange={handleCropTypeChange}
                />
              </label>
              <label htmlFor="pulses">
                Pulses
                <input
                  type="checkbox"
                  id="pulses"
                  value="Pulses"
                  onChange={handleCropTypeChange}
                />
              </label>
            </div>
          </div>
          {/* Nitrogen */}
          <div className="col-span-2 sm:col-span-1">
            <label
              className="block text-gray-700 font-bold mb-2"
              htmlFor="nitrogen"
            >
              Nitrogen (%):
            </label>
            <input
              type="text"
              id="nitrogen"
              placeholder="Enter Nitrogen Percentage"
              className="w-full bg-white border border-black rounded-lg p-2 outline-none"
              value={nitrogen}
              onChange={(e) => setNitrogen(e.target.value)}
            />
          </div>
          {/* Potassium */}
          <div className="col-span-2 sm:col-span-1">
            <label
              className="block text-gray-700 font-bold mb-2"
              htmlFor="potassium"
            >
              Potassium (%):
            </label>
            <input
              type="text"
              id="potassium"
              placeholder="Enter Potassium Percentage"
              className="w-full bg-white border border-black rounded-lg p-2 outline-none"
              value={potassium}
              onChange={(e) => setPotassium(e.target.value)}
            />
          </div>
          {/* Phosphorus */}
          <div className="col-span-2 sm:col-span-1">
            <label
              className="block text-gray-700 font-bold mb-2"
              htmlFor="phosphorus"
            >
              Phosphorus (%):
            </label>
            <input
              type="text"
              id="phosphorus"
              placeholder="Enter Phosphorus Percentage"
              className="w-full bg-white border border-black rounded-lg p-2 outline-none"
              value={phosphorus}
              onChange={(e) => setPhosphorus(e.target.value)}
            />
          </div>
          {/* Submit Button */}
          <div className="col-span-2">
            <button
              className="bg-blue-500 hover:bg-blue-600 text-white p-3 rounded-lg w-[60%] sm:w-64 mx-auto block"
              onClick={handleSubmit}
            >
              Submit
            </button>
          </div>
        </div>
      </div>
      {/* Render fertilizer prediction result */}
      {!error && fertilizerResult && (
        <div className="mt-4">
          <p className="text-center text-xl font-bold font-mono text-green-700">
            Recommended Fertilizer: {fertilizerResult}
          </p>
        </div>
      )}
      {/* Render error message if there is an error */}
      {error && (
        <div className="mt-4">
          <p className="text-center font-mono font-bold text-red-500">
            {error}
          </p>
        </div>
      )}
    </div>
  );
};

export default FertilizerPrediction;
