import React, { useState } from "react";
import { Link } from "react-router-dom";

const Navbar = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [showMenu, setShowMenu] = useState(false);

  const handleSearch = () => {
    if (searchQuery.trim() !== "") {
      window.location.href = `https://www.google.com/search?q=${encodeURIComponent(
        searchQuery
      )}`;
    }
  };

  const handleInputChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  return (
    <nav className="bg-blue-700 p-4">
      <div className="container mx-auto flex flex-col md:flex-row justify-between items-center">
        <div className="mb-4 md:mb-0 flex flex-wrap">
          <button
            className="text-white md:hidden"
            onClick={toggleMenu}
            aria-label="Toggle Menu"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-6 w-6"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d={showMenu ? "M6 18L18 6M6 6l12 12" : "M4 6h16M4 12h16m-7 6h7"}
              />
            </svg>
          </button>
          <div className={`md:flex ${showMenu ? "block" : "hidden"}`}>
            <Link
              to="/"
              className="text-white font-semibold text-lg mr-4 mb-2 md:mb-0 bg-green-500 hover:bg-green-400 hover:text-white px-4 py-2 rounded-lg transition duration-300"
            >
              Home
            </Link>
            <Link
              to="/weather"
              className="text-white font-semibold text-lg mr-4 mb-2 md:mb-0 bg-green-500 hover:bg-green-400 hover:text-white px-4 py-2 rounded-lg transition duration-300"
            >
              Weather
            </Link>
            <Link
              to="/crop-prediction"
              className="text-white font-semibold text-lg mr-4 mb-2 md:mb-0 bg-green-500 hover:bg-green-400 hover:text-white px-4 py-2 rounded-lg transition duration-300"
            >
              Crop
            </Link>
            <Link
              to="/disease-prediction"
              className="text-white font-semibold text-lg mr-4 mb-2 md:mb-0 bg-green-500 hover:bg-green-400 hover:text-white px-4 py-2 rounded-lg transition duration-300"
            >
              Disease
            </Link>
          </div>
        </div>
        <div className="flex items-center">
          <div className="bg-white rounded-full p-2 flex items-center mr-4">
            <input
              type="text"
              placeholder="Search..."
              className="w-full md:w-64 p-2 outline-none"
              value={searchQuery}
              onChange={handleInputChange}
            />
            <button
              className="bg-black text-white p-2 rounded-full ml-2"
              onClick={handleSearch}
            >
              Search
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
